import './App.css';
import {useState, useEffect} from 'react'
import NavBar from "./components/navbar"
import Home from "./components/home"
import AboutView from './components/aboutView';
import {Switch, Route} from 'react-router-dom' 
import Search from './components/search'

//const API_KEY = 45e3e92747353d02484018a30548fa3d

function App() {

  const [ searchResults, setSearchResults] = useState([]);
  const [ searchText, setSearchText] = useState('');

  useEffect(() => {
    console.log(searchText, "is the sea rch text")
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=45e3e92747353d02484018a30548fa3d&language=en-US&query=${searchText}&page=1&include_adult=false`)
      .then(response => response.json())
      .then(data => {
        // console.log(data)
        setSearchResults(data.results)
      })

  }, [searchText])

  return (
    <div >
      <NavBar searchText={searchText} setSearchText={setSearchText} />
      <Switch>

        <Route exact path="/" component={Home} />

        <Route path="/about" component={AboutView} />

        <Route path="/search">
          <Search searchWord={searchText} />
        </Route>

      </Switch>
    </div>
  );
}

export default App;
