import Hero from './hero'


const MovieCard = ({movie}) => {

  const posterPath = `https://image.tmdb.org/t/p/w500${movie.poster_path}`;


  return (
    <div className="card" style="width: 18rem;">
      <img src="" className="card-img-top" alt={movie.original_title} />
      <div className="card-body">
        <h5 className="card-title">{movie.original_title}</h5>
        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        <a href="/njk" className="btn btn-primary">Go somewhere</a>
      </div>
    </div>

  )
}


const Search = ({searchWord, searchResults}) => {

    const title = `You are searching for ${searchWord}`

    const ResultsHTML = searchResults.map((data, i) =>{
      return <div key={i}>{data.original_title}</div>

    })


    return (

      <>
      <Hero text={title}/>
      {ResultsHTML}
      </>
    )
  }


export default Search
